import pandas as pd
import requests
import re
import os


class movieLoader:

    def __init__(self):
        self.movies = []
        self.key = "352fb6cebe850dcd6a8414d4a54a7abd"
        self.dataPath = "../movies-database/transformedFiles/movieData.csv"

    def extract_year(self, title):
        match = re.search(r"\((\d{4})\)$", title)
        if match:
            year = match.group(1)
            title = re.sub(r"\s*\(\d{4}\)$", "", title)
            return title.strip(), int(year)
        return title.strip(), None
    
    def normalize_title(self, title):
        if isinstance(title, str) and "," in title:
            parts = title.rsplit(",", 1)
            if len(parts) == 2:
                name, article = parts[0].strip(), parts[1].strip()
                if article in ["The", "A", "An"]:
                    return f"{article} {name}"
        return title
    
    

    def load(self):
        

        if os.path.exists(self.dataPath):
            self.movies = pd.read_csv(self.dataPath)
            return

        # Load raw data
        mov = pd.read_csv("../movies-database/ml-25m/movies.csv")
        mov[["title", "year"]] = mov["title"].apply(self.extract_year).apply(pd.Series)
        mov["title"] = mov["title"].apply(self.normalize_title)


        links = pd.read_csv("../movies-database/ml-25m/links.csv")
        ratings = pd.read_csv("../movies-database/ml-25m/ratings.csv")

        # Merge movie info with links (left keeps all movies, right would filter)
        movies = pd.merge(mov, links, how="left", on="movieId")



        # Aggregate ratings
        ratings_summary = ratings.groupby("movieId").agg(
            count=("rating", "count"),
            avgRating=("rating", "mean")
        ).reset_index()

        ratings_summary["avgRating"] = ratings_summary["avgRating"].round(2)

        # Merge with ratings
        movies = pd.merge(movies, ratings_summary, how="left", on="movieId")
        movies["img"] = ""        


        self.movies = movies
        self.saveMovies()


    def saveMovies(self):
        # Save for future use
        os.makedirs("../movies-database/transformedFiles", exist_ok=True)
        self.movies.to_csv(self.dataPath, index=False)

    def loadPicture(self, movieId, imdbid, tmdbid):
        url = f"https://api.themoviedb.org/3/find/{imdbid}"
        params = {
            "api_key": self.key,
            "external_source": "imdb_id"
        }

        try:
            response = requests.get(url, params=params)
            data = response.json()
            poster_path = data["movie_results"][0]["poster_path"]
            full_url = f"https://image.tmdb.org/t/p/w500{poster_path}"
            self.movies.loc[self.movies["movieId"] == movieId, "img"] = full_url
            print(full_url)
            return full_url

        except:
            try:
                url = f"https://api.themoviedb.org/3/movie/{tmdbid}"
                params = {"api_key": self.key}
                response = requests.get(url, params=params)
                data = response.json()
                poster_path = data["poster_path"]
                full_url = f"https://image.tmdb.org/t/p/w500{poster_path}"
                self.movies.loc[self.movies["movieId"] == movieId, "img"] = full_url
                print(full_url)
                return full_url
            except Exception as e:
                print(f"[Poster Error] movieId={movieId}, imdbId={imdbid}, tmdbId={tmdbid}")
                print(f"[Exception] {type(e).__name__}: {e}")
                return "data:image/jpeg;base64,"
        

